    private void checkPosition (Integer position){
    if (position == 0) {
        loadEurope();
    } else if (position == 1) {
        loadAzia();
    } else if (position == 2) {
        loadAfrica();
    } else if (position == 3) {
        loadNouthAmerica();
    } else if (position == 4) {
        loadSouthAmerica();
    }
}






